.preloader-custom {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #0d0d0d 0%, #1a1a1a 100%);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 9999;
  transition: opacity 0.5s ease-out;
}

.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  padding: 20px;
}

.loading-image {
  width: 140px;
  height: 140px;
  border-radius: 50%;
  object-fit: cover;
  animation: pulse 2s ease-in-out infinite, rotate 3s linear infinite;
  box-shadow: 0 0 20px rgba(255, 82, 82, 0.5);
  border: 3px solid #000;
  background: #000;
  position: relative;
}

.loading-image::before {
  content: '';
  position: absolute;
  top: -6px;
  left: -6px;
  right: -6px;
  bottom: -6px;
  border: 2px solid rgba(255, 82, 82, 0.3);
  border-radius: 50%;
  animation: rotate 8s linear infinite reverse;
}

.loading-text {
  color: #ff5252;
  font-size: 20px;
  font-weight: 600;
  letter-spacing: 1px;
  animation: fadeInOut 2s ease-in-out infinite;
}

.loading-dots {
  display: inline-block;
}

.loading-dots::after {
  content: '';
  animation: dots 1.5s steps(4, end) infinite;
}

@keyframes pulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.1); opacity: 0.8; }
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes fadeInOut {
  0%, 100% { opacity: 0.5; }
  50% { opacity: 1; }
}

@keyframes dots {
  0% { content: ''; }
  25% { content: '.'; }
  50% { content: '..'; }
  75% { content: '...'; }
  100% { content: ''; }
}

/* å¤‡ç”¨åŠ è½½åŠ¨ç”» - å¦‚æœå›¾ç‰‡åŠ è½½å¤±è´¥ */
.loading-spinner {
  width: 50px;
  height: 50px;
  border: 3px solid rgba(255, 82, 82, 0.3);
  border-top: 3px solid #ff5252;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* éšè—åŠ è½½å™¨ */
.preloader-taiji.hide {
  opacity: 0;
  pointer-events: none;
}

