(function() {
    'use strict';

    // åŠ è½½åŠ¨ç”»é…ç½® - ä½¿ç”¨é…ç½®æ–‡ä»¶ä¸­çš„è®¾ç½®
    const LOADING_CONFIG = {
        // ä½¿ç”¨é…ç½®æ–‡ä»¶ä¸­çš„ä¸»åŠ è½½å›¾ç‰‡
        imageSrc: LOADING_IMAGES.primary, // ç°åœ¨æ˜¯ '/img/custom-loading.gif'
        // ä½¿ç”¨é…ç½®æ–‡ä»¶ä¸­çš„å¤‡ç”¨å›¾ç‰‡
        fallbackImage: LOADING_IMAGES.fallback, // ç°åœ¨æ˜¯ '/img/custom-loading2.gif'
        // ä½¿ç”¨é…ç½®æ–‡ä»¶ä¸­çš„åŠ è½½æ–‡å­—
        loadingText: LOADING_IMAGES.text, // ç°åœ¨æ˜¯ 'æ­£åœ¨åŠ è½½ä¸­'
        // ä½¿ç”¨é…ç½®æ–‡ä»¶ä¸­çš„æŒç»­æ—¶é—´
        minDuration: LOADING_IMAGES.duration // ç°åœ¨æ˜¯ 1500ms
    };

    // åˆ›å»ºåŠ è½½åŠ¨ç”»HTML
    function createPreloader() {
        const preloader = document.createElement('div');
        preloader.className = 'preloader-custom';

        // åˆ›å»ºåŠ è½½å®¹å™¨
        const loadingContainer = document.createElement('div');
        loadingContainer.className = 'loading-container';

        // åˆ›å»ºå›¾ç‰‡å…ƒç´ 
        const img = document.createElement('img');
        img.className = 'loading-image';
        img.src = LOADING_CONFIG.imageSrc;
        img.alt = 'Loading';

        // å›¾ç‰‡åŠ è½½é”™è¯¯å¤„ç†
        img.onerror = function() {
            console.log('åŠ è½½å›¾ç‰‡å¤±è´¥ï¼Œä½¿ç”¨å¤‡ç”¨å›¾ç‰‡');
            this.src = LOADING_CONFIG.fallbackImage;

            // å¦‚æœå¤‡ç”¨å›¾ç‰‡ä¹Ÿå¤±è´¥ï¼Œä½¿ç”¨CSSåŠ¨ç”»
            this.onerror = function() {
                console.log('å¤‡ç”¨å›¾ç‰‡ä¹Ÿå¤±è´¥ï¼Œä½¿ç”¨CSSåŠ è½½åŠ¨ç”»');
                const spinner = document.createElement('div');
                spinner.className = 'loading-spinner';

                // æ›¿æ¢å›¾ç‰‡ä¸ºCSSåŠ¨ç”»
                if (this.parentNode) {
                    this.parentNode.replaceChild(spinner, this);
                }
            };
        };

        // åˆ›å»ºåŠ è½½æ–‡å­—
        const text = document.createElement('div');
        text.className = 'loading-text';
        text.innerHTML = `${LOADING_CONFIG.loadingText}<span class="loading-dots"></span>`;

        // ç»„è£…å…ƒç´ 
        loadingContainer.appendChild(img);
        loadingContainer.appendChild(text);
        preloader.appendChild(loadingContainer);

        document.body.appendChild(preloader);
        return preloader;
    }

    // éšè—åŠ è½½åŠ¨ç”»
    function hidePreloader(preloader) {
        setTimeout(() => {
            preloader.classList.add('hide');
            setTimeout(() => {
                if (preloader.parentNode) {
                    preloader.parentNode.removeChild(preloader);
                }
            }, 500);
        }, 1000); // è‡³å°‘æ˜¾ç¤º1ç§’
    }

    // é¡µé¢åŠ è½½å®Œæˆåéšè—åŠ¨ç”»
    document.addEventListener('DOMContentLoaded', function() {
        const preloader = createPreloader();

        // ç­‰å¾…æ‰€æœ‰èµ„æºåŠ è½½å®Œæˆ
        window.addEventListener('load', function() {
            hidePreloader(preloader);
        });

        // å¦‚æœ3ç§’åé¡µé¢è¿˜æ²¡åŠ è½½å®Œï¼Œä¹Ÿéšè—åŠ¨ç”»
        setTimeout(() => {
            hidePreloader(preloader);
        }, 3000);
    });

    // å¹³æ»‘æ»šåŠ¨
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            const target = document.querySelector(this.getAttribute('href'));
            if (target) {
                target.scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
        });
    });

    // æ·»åŠ é¡µé¢è¿‡æ¸¡æ•ˆæœ
    document.addEventListener('pjax:send', function() {
        const preloader = createPreloader();
    });

    document.addEventListener('pjax:complete', function() {
        const preloader = document.querySelector('.preloader-taiji');
        if (preloader) {
            hidePreloader(preloader);
        }
    });

    // æ‰“å­—æœºæ•ˆæœ
    function typeWriter(element, text, speed = 100) {
        let i = 0;
        element.innerHTML = '';

        function type() {
            if (i < text.length) {
                element.innerHTML += text.charAt(i);
                i++;
                setTimeout(type, speed);
            }
        }
        type();
    }

    // ä¸ºæ ‡é¢˜æ·»åŠ æ‰“å­—æœºæ•ˆæœ
    const siteTitle = document.querySelector('.site-title');
    if (siteTitle && siteTitle.textContent) {
        const originalText = siteTitle.textContent;
        typeWriter(siteTitle, originalText, 150);
    }

    // é¼ æ ‡è·Ÿéšæ•ˆæœ
    let mouseX = 0, mouseY = 0;
    document.addEventListener('mousemove', function(e) {
        mouseX = e.clientX;
        mouseY = e.clientY;
    });

    // åˆ›å»ºé¼ æ ‡ç‚¹å‡»æ•ˆæœ
    document.addEventListener('click', function(e) {
        const ripple = document.createElement('div');
        ripple.style.position = 'fixed';
        ripple.style.left = e.clientX + 'px';
        ripple.style.top = e.clientY + 'px';
        ripple.style.width = '10px';
        ripple.style.height = '10px';
        ripple.style.background = 'rgba(255, 82, 82, 0.5)';
        ripple.style.borderRadius = '50%';
        ripple.style.transform = 'translate(-50%, -50%)';
        ripple.style.pointerEvents = 'none';
        ripple.style.animation = 'ripple 0.6s ease-out';

        document.body.appendChild(ripple);

        setTimeout(() => {
            if (ripple.parentNode) {
                ripple.parentNode.removeChild(ripple);
            }
        }, 600);
    });

    // æ·»åŠ CSSåŠ¨ç”»
    const style = document.createElement('style');
    style.textContent = `
        @keyframes ripple {
            to {
                width: 100px;
                height: 100px;
                opacity: 0;
            }
        }
    `;
    document.head.appendChild(style);

})();

